mapper.select:
SELECT F.ID, NOTICE_TITLE, NOTICE_REGISTER_DATE
FROM
    (
        SELECT ROWNUM R, ID, NOTICE_TITLE, NOTICE_REGISTER_DATE
        FROM
                (
                    SELECT ROWNUM C, ID, NOTICE_TITLE, NOTICE_REGISTER_DATE
                    FROM TBL_NOTICE
                    ORDER BY ID DESC
                )

        WHERE ROWNUM <= #{page} * #{rowCount}
    )
F WHERE R > (#{page} - 1) * #{rowCount}

SELECT ID, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_REGISTER_DATE
FROM TBL_NOTICE
WHERE ID = #{id}

mapper.update:
UPDATE TBL_NOTICE
SET NOTICE_TITLE = #{noticeTitle}, NOTICE_CONTENT = #{noticeContent}
WHERE ID = #{id}

mapper.select:
SELECT ID, INQUIRY_ID, ANSWER_CONTENT, ANSWER_REGISTER_DATE
FROM tbl_answer
WHERE INQUIRY_ID = #{inquiryId}

mapper.update:
UPDATE TBL_ANSWER
SET ANSWER_CONTENT = #{answerContent}
WHERE ID = #{id}

mapper.select:
SELECT F.ID, MEMBER_STATUS, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_PHONE_NUMBER, MEMBER_REGISTER_DATE
FROM
    (
        SELECT ROWNUM R, ID, MEMBER_STATUS, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_PHONE_NUMBER, MEMBER_REGISTER_DATE
        FROM
                (
                    SELECT ROWNUM C, ID, MEMBER_STATUS, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_PHONE_NUMBER, MEMBER_REGISTER_DATE
                    FROM TBL_MEMBER
                    ORDER BY ID DESC
                )

        WHERE ROWNUM <= #{page} * #{rowCount}
    )
F WHERE R > (#{page} - 1) * #{rowCount}

SELECT F.ID, LECTURE_TITLE, LECTURE_DATE, LECTURE_TIME, LECTURE_LOCATION, LECTURE_TOTAL_PERSONNEL
FROM
    (
        SELECT ROWNUM R, ID, LECTURE_TITLE, LECTURE_DATE, LECTURE_TIME, LECTURE_LOCATION, LECTURE_TOTAL_PERSONNEL
        FROM
                (
                    SELECT ROWNUM C, ID, LECTURE_TITLE, LECTURE_DATE, LECTURE_TIME, LECTURE_LOCATION, LECTURE_TOTAL_PERSONNEL
                    FROM TBL_LECTURE
                    ORDER BY ID DESC
                )

        WHERE ROWNUM <= #{page} * #{rowCount}
    )
F WHERE R > (#{page} - 1) * #{rowCount}
